import timeimport randomimport os#Class variablesgalaxy = []available = []users =[]passwords = []#Not stored variablea = 0f = 10#Subclasses of gameclass planet:    def __init__(self, size, planetType, s, p):        self.size = size        self.planetType = planetType        self.coordinates = [s, p]        self.user = ""        #Resources        self.metal = [200,0]        self.no_metal = [200,0]        self.gas = [200,0]        #Limits        self.metal_limit = 1000        self.no_metal_limit = 1000        self.gas_limit = 1000        #Buildings        self.metal_mine = building(0, 100, 100)        self.no_metal_mine = building(0, 100, 100)        self.gas_refinery = building(0, 100, 100)        self.metal_repository = building(0, 500, 500)        self.no_metal_repository = building(0, 500, 500)        self.gas_tank = building(0, 500, 500)    def __repr__(self):    	return "(" + str(self.coordinates) + ", " + str(self.size) + ", " + str(self.planetType) + ")"    def reclaim(self, user):        t = int(time.time())        self.user = user        self.metal[1] = t        self.no_metal[1] = t        self.gas[1] = tclass building:    def __init__(self, level, metalCost, nonmetalCost):        self.level = level        self.metal_cost = metalCost        self.no_metal_cost = nonmetalCost    def upgrade(self):        self.level += 1        self.metal_cost *= 2        self.no_metal_cost *= 2class research:    def __init__(self, name, level, metalCost, nonmetalCost, gasCost):        self.name = name        self.level = level        self.metal_cost = metalCost        self.no_metal_cost = nonmetalCost        self.gas_cost = gasCostclass ship:    def __init__(self, shipname, shield, speed, metalCost, nonmetalCost, gasCost):        self.shipname = shipname        self.power = power        self.speed = speed        self.metal_cost = metalCost        self.no_metal_cost = nonmetalCost        self.gas_cost = gasCostclass mission:    def __init__(self, missionType):        self.missionType = missionTypeclass user:    def __init__(self, username, password):        self.username = username        self.password = password        self.points = 0        self.planets = []    def reclaim(self, n):    	self.planets.append(n)def generate_universe(ss):    for s in range(ss):        system = []        for p in range(15):            if random.randrange(0, 2) == 1:                pl = planet(random.randrange(100, 120), random.randrange(4), s, p)                system.append(pl)                available.append([s, p])            else:                system.append(0)        galaxy.append(system)def save():    print("pending")def regenerate_universe(g, u , p):    l = list(map(int, input().rstrip().split()))def rand_assign(user):    pc = random.choice(available)    available.remove(pc)    galaxy[pc[0]][pc[1]].reclaim(user)    user.reclaim(galaxy[pc[0]][pc[1]])def build(planet, building):    global f    t = int(time.time())    balance = int((t - planet.metal[1])*planet.metal_mine.level*f) + planet.metal[0]    if balance >= planet.metal_limit:        balance =  planet.metal_limit    if building.metal_cost <= balance:        balance2 = int((t - planet.no_metal[1])*planet.no_metal_mine.level*f) + planet.no_metal[0]        if balance2 >= planet.no_metal_limit:            balance2 =  planet.no_metal_limit        if building.no_metal_cost <= balance2:            planet.metal[0] = balance - building.metal_cost            planet.metal[1] = t            planet.no_metal[0] = balance2 - building.no_metal_cost            planet.no_metal[1] = t            if building == planet.metal_repository:            	planet.metal_limit *= 2            if building == planet.no_metal_repository:            	planet.no_metal_limit *= 2            if building == planet.gas_tank:            	planet.gas_limit *= 2            building.upgrade()            print("Upgrede succesful")        else:            print("Not enough no_metals")    else:        print("Not enough metal")def show(planet):    global f    t = int(time.time())    balance = int((t - planet.metal[1])*planet.metal_mine.level*f) + planet.metal[0]    if balance >= planet.metal_limit:        balance =  planet.metal_limit    balance2 = int((t - planet.no_metal[1])*planet.no_metal_mine.level*f) + planet.no_metal[0]    if balance2 >= planet.no_metal_limit:        balance2 =  planet.no_metal_limit    balance3 = int((t - planet.gas[1])*planet.gas_refinery.level*f) + planet.gas[0]    if balance3 >= planet.gas_limit:        balance3 =  planet.gas_limit    print("Metal = " + str(balance) + "/" + str(planet.metal_limit) + " | No metal = " + str(balance2) + "/" + str(planet.no_metal_limit) + " | Gas = " + str(balance3) + "/" + str(planet.gas_limit))def clear():	a = True	if a:		os.system('clear')def start(userx):    a = 0    user = userx    p = user.planets[0]    while True:        print("\nWhat do u wanna do?\n" +              "[1] Build\n" +              "[2] Research\n" +              "[3] Explore\n" +              "[4] Ship\n" +              "[5] Inform\n" +              "[6] Switch\n" +              "[7] Logout\n")        a = int(input())        clear()        if a == 1:            print("What do u wanna build?\n" +                  "[1] Buildings\n" +                  "[2] Shipping\n")            a = int(input())            clear()            if a == 1:                print("What do u wanna build?\n" +                      "[1] Metal mine\n" +                      "[2] No metal mine\n" +                      "[3] Gas Refinery\n" +                      "[4] Metal repository\n" +                      "[5] No metal repository\n" +                      "[6] Gas tank\n")                a = int(input())                clear()                if a == 1:                    b = p.metal_mine                elif a == 2:                    b = p.no_metal_mine                elif a == 3:                    b = p.gas_refinery                elif a == 4:                	b = p.metal_repository                elif a == 5:                	b = p.no_metal_repository                elif a == 6:                	b = p.gas_tank                else:                    print("Wrong answer")                    continue                print("Are u sure? [1] SÃ­  |  [0] No\n")                print("Metal cost = " + str(b.metal_cost) + " | No metal cost = " + str(b.no_metal_cost) + "\n")                a = int(input())                clear()                if a == 1:                	build(p, b)            elif a == 2:                print("Coming soon")            else:                print("Wrong answer")        elif a == 5:            show(p)        elif a == 7:            break        else:        	print('Coming soon')#Testing of gamegenerate_universe(100)u = user("reluro", "kk12345")rand_assign(u)start(u)#Game beginning##try:##    uf = open("users.game", "r+")##    gf = open("game.game", "r+")##    pf = open("password.game", "r+")##except:##    print("Data not found...\n" +##          "Your username will be created in a new game file.")##    uf = open("users.game", "w+")##    gf = open("game.game", "w+")##    pf = open("password.game", "w+")##    a = 3##while True:##    if a == 0:##        print("What do u wanna do?\n" +##              "[0] Login\n" +##              "[1] Register\n" +##              "[2] Exit\n")##        try:##            a = int(input())##			clear()##        except:##            a = -1##    if a == 0 or  a == 1 or a == 3:##        print("Enter username:")##        u = input()##        if u == "" or u == "-":##            print("Invalid user. Try again")##            a = 0##            continue##        print("Enter password:")##        p = input()##		clear()##        if a != 3:##            ufd = uf.r